INSERT INTO `check_examples` VALUES(1, 1, '1', '<code>img</code> element with an <code>alt</code> attribute.', '<?xml version="1.0" encoding="UTF-8"?>\r<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd">\r<html xmlns="http://www.w3.org/1999/xhtml" xml:lang="en" lang="en">\r<head>\r<meta http-equiv="Content-Type" content="text/xhtml; charset=UTF-8" />\r<title>Testfile - Check #1 - Negative</title>\r</head>\r<body>\r<p><img src="rex.jpg" alt="A black and brown cat named Rex."/></p>\r</body>\r</html>');
