INSERT INTO `check_examples` VALUES(7, 4, '1', 'IMG is non-decorative and has valid Alt text.', '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN" "http://www.w3.org/TR/REC-html40/strict.dtd">\r<html lang="en">\r<head>\r<title>OAC Testfile - Check #4 - Negative</title>\r</head>\r<body>\r<p>We would like to adopt another pet and are looking for one that is similar to what''s shown in the picture.</p>\r<img src="rex.jpg" alt="large brown and black cat named Rex" width="150" height="105"/>\r</body>\r</html>');
INSERT INTO `check_examples` VALUES(8, 4, '0', 'IMG is non-decorative but has empty Alt text.', '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN" "http://www.w3.org/TR/REC-html40/strict.dtd">\r<html lang="en">\r<head>\r<title>OAC Testfile - Check #4 - Positive</title>\r</head>\r<body>\r<p>We would like to adopt another pet and are looking for one that is similar to what''s shown in the picture.</p>\r<img src="rex.jpg" alt="" width="150" height="105"/>\r</body>\r</html>');
