INSERT INTO `check_examples` VALUES(11, 6, '1', '<code>img</code> has alt text that does not contain any placeholder text.', '<?xml version="1.0" encoding="UTF-8"?>\n<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd">\n<html xmlns="http://www.w3.org/1999/xhtml" xml:lang="en" lang="en">\n<head>\n<meta http-equiv="Content-Type" content="text/xhtml; charset=UTF-8" />\n<title>OAC Testfile - Check #6 - Negative</title>\n</head>\n<body>\n<p><img src="rex.jpg" alt="Photo of a brown and black cat named Rex."/></p>\n</body>\n</html>');
