INSERT INTO `check_examples` VALUES(13, 7, '1', '<code>img</code> is contained by A element, anchor has no other text and Alt text is not empty.', '<?xml version="1.0" encoding="UTF-8"?>\r<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd">\r<html xmlns="http://www.w3.org/1999/xhtml" xml:lang="en" lang="en">\r<head>\r<title>OAC Testfile - Check #7 - Negative</title>\r</head>\r<body>\r<p><a href="rex.html"><img src="rex.jpg" alt="a story about Rex the cat"/></a></p>\r</body>\r</html>');
INSERT INTO `check_examples` VALUES(14, 7, '0', '<code>img</code> is contained by A element, anchor has no other text and Alt text is empty.', '<?xml version="1.0" encoding="UTF-8"?>\r<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd">\r<html xmlns="http://www.w3.org/1999/xhtml" xml:lang="en" lang="en">\r<head>\r<title>OAC Testfile - Check #7 - Positive</title>\r</head>\r<body>\r<p><a href="rex.html"><img src="rex.jpg" alt=""/></a></p>\r</body>\r</html>');
INSERT INTO `check_examples` VALUES(15, 8, '1', 'Image is not fully described in Alt text but image has a <code>longdesc</code> attribute.', '<?xml version="1.0" encoding="UTF-8"?>\r<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd">\r<html xmlns="http://www.w3.org/1999/xhtml" xml:lang="en" lang="en">\r<head>\r<title>OAC Testfile - Check #8.2 - Negative</title>\r</head>\r<body>\r<p>The text in this document does not fully describe the image shown here but the image contains a longdesc attribute linking to a text file that does describe the image. <img src="chart.gif" alt="a complex chart" longdesc="chart.html"/></p>\r</body>\r</html>');
INSERT INTO `check_examples` VALUES(16, 8, '0', 'Image is not fully described in Alt text and a long description is not provided.', '<?xml version="1.0" encoding="UTF-8"?>\r<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd">\r<html xmlns="http://www.w3.org/1999/xhtml" xml:lang="en" lang="en">\r<head>\r<title>OAC Testfile - Check #8.1 - Positive</title>\r</head>\r<body>\r<p>The text in this document does not fully describe the complex image shown here.\r<img src="chart.gif" alt="a complex chart" /></p>\r</body>\r</html>');
INSERT INTO `check_examples` VALUES(17, 9, '1', '<code>img</code> with a <code>longdesc</code> and d-link.', '<?xml version="1.0" encoding="UTF-8"?>\r\n<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd">\r\n<html xmlns="http://www.w3.org/1999/xhtml" xml:lang="en" lang="en">\r\n<head>\r\n<meta http-equiv="Content-Type" content="text/xhtml; charset=UTF-8" />\r\n<title>OAC Testfile - Check #9 - Positive</title>\r\n</head>\r\n<body>\r\n<p><img src="rex.jpg" alt="photo of Rex the cat" width="300" height="300" longdesc="rex.html" />\r\n<a href="rex.html">[d]</a></p>\r\n</body>\r\n</html>');
