INSERT INTO `check_examples` VALUES(251, 135, '1', 'Image contains math and document has MathML.', '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.1 plus MathML 2.0//EN" "http://www.w3.org/TR/MathML2/dtd/xhtml-math11-f.dtd" [\r  <!ENTITY mathml "http://www.w3.org/1998/Math/MathML"> ]>\r\n<html lang="en">\r\n	<head>\r\n		<title>Testfile - Check #135 - Negative</title>\r\n	</head>\r\n	<body>\r\n		<p>The following image shows the solution to the quadratic equation: begin fraction minus b plus or \r\n			minus begin square root of b squared minus four a c end square root over 2 a \r\n			end fraction</p>\r\n		<img src="quadeqn.png" alt="solution to the quadratic equation" width="179" height="63">\r\n		<math xmlns=''http://www.w3.org/1998/Math/MathML''>\r\n			<mfrac>\r\n				<mrow>\r\n					<mrow>\r\n						<mo>-</mo>\r\n						<mi>b</mi>\r\n					</mrow>\r\n					<mo>&pm;</mo>\r\n					<msqrt>\r\n						<mrow>\r\n							<msup>\r\n								<mi>b</mi>\r\n								<mn>2</mn>\r\n							</msup>\r\n							<mo>-</mo>\r\n							<mrow>\r\n								<mn>4</mn>\r\n								<mo>&it;</mo>\r\n								<mi>a</mi>\r\n								<mo>&it;</mo>\r\n								<mi>c</mi>\r\n							</mrow>\r\n						</mrow>\r\n					</msqrt>\r\n				</mrow>\r\n				<mrow>\r\n					<mn>2</mn>\r\n					<mo>&it;</mo>\r\n					<mi>a</mi>\r\n				</mrow>\r\n			</mfrac>\r\n		</math>\r\n	</body>\r\n</html>');
INSERT INTO `check_examples` VALUES(252, 135, '0', 'Image contains math but document has no MathML.', '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN" "http://www.w3.org/TR/REC-html40/strict.dtd">\r\n<html lang="en">\r\n	<head>\r\n		<title>Testfile - Check #135 - Positive</title>\r\n	</head>\r\n	<body>\r\n		<p>The following image shows the solution to the quadratic equation: begin fraction minus b plus or minus begin square root of b squared minus four a c end square root over 2 a end fraction</p>\r\n\r\n		<img src="quadeqn.png" alt="solution to the quadratic equation" width="179" height="63">\r\n	</body>\r\n</html>');
