INSERT INTO `check_examples` VALUES(442, 246, '1', 'Required fields (first name and last name) are marked as "required".', '<?xml version="1.0" encoding="UTF-8"?>\n<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd">\n<html xmlns="http://www.w3.org/1999/xhtml" xml:lang="en" lang="en">\n<head>\n<meta http-equiv="Content-Type" content="text/xhtml; charset=UTF-8" />\n<title>ATRC Testfile - Check #246.2 - Negative</title>\n</head>\n<body>\n\n<form action="http://www.test.com">\n\n<p>\n<label for="firstname">First Name (required):</label>\n<input type="text" id="firstname" /><br/>\n<label for="lastname">Last Name (required):</label>\n<input type="text" id="lastname" /><br/>\n<label for="colour">Favourite Colour:</label>\n<input type="text" id="colour" /><br/>\n<input type="submit" value="submit"/>\n</p>\n\n</form>\n\n</body>\n</html>');
