INSERT INTO `check_examples` VALUES(444, 247, '1', 'Checkbox group contains both <code>fieldset</code> and <code>legend</code> elements.', '<?xml version="1.0" encoding="UTF-8"?>\r<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd">\r<html xmlns="http://www.w3.org/1999/xhtml" xml:lang="en" lang="en">\r<head>\r\r\r<meta http-equiv="Content-Type" content="text/xhtml; charset=UTF-8" />\r<META content="text/xhtml; charset=UTF-8" http-equiv="Content-Type">\r\r<title>ATRC Testfile - Check #247.2 - Negative</title>\r</head>\r<body>\r\r<form action="http://example.com/donut" method="post">\r<fieldset>\r<legend>Donuts Requested (check all that apply)</legend>\r<p>\r<input type="checkbox" name="flavour" id="choc" value="chocolate" />\r<label for="choc">Chocolate</label><br/>\r<input type="checkbox" name="flavour" id="cream" value="cream" />\r<label for="cream">Cream Filled</label><br/>\r<input type="checkbox" name="flavour" id="honey" value="honey" />\r<label for="honey">Honey Glazed</label>\r</p>\r</fieldset>\r<p><input type="submit" value="Purchase Donuts"/></p>\r</form>\r\r</body>\r</html>');
