INSERT INTO `check_examples` VALUES(498, 274, '1', 'Window change occurs only after user selects form submit button.', '<?xml version="1.0" encoding="UTF-8"?>\n<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd">\n<html xmlns="http://www.w3.org/1999/xhtml" xml:lang="en" lang="en">\n<head>\n<meta http-equiv="Content-Type" content="text/xhtml; charset=UTF-8" />\n<title>Testfile - Check #274.2 - Positive</title>\n</head>\n<body>\n\n<form action="http://www.example.com" method="post">\n<p>\n<select name="flavour">\n<option>Choose A Flavour</option>\n<option value="c">chocolate</option>\n<option value="v">vanilla</option>\n<option value="m">mango</option>\n</select><br/>\n<input type="submit" value="submit flavour" />\n</p>\n</form>\n\n</body>\n</html>');
