INSERT INTO `language_text` VALUES('eng', '_check', '_PROCEDURE_126', '1. Check all <code>input</code> elements that have a <code>type</code> attribute value of "text".\n2. Check if the VALUE attribute value on the <code>input</code> element contains any text.', '2009-03-18 13:48:49', '');
INSERT INTO `language_text` VALUES('eng', '_check', '_PROCEDURE_127', '1. Check the text equivalent for all <code>object</code> elements in the content.', '2009-03-18 13:48:49', '');
