INSERT INTO `language_text` VALUES('eng', '_check', '_RATIONALE_13', 'Some people may not be able to access the links that are part of an image map. They require a duplicate set of links.', '2008-11-24 13:02:09', '');
INSERT INTO `language_text` VALUES('eng', '_check', '_RATIONALE_131', 'The proper marking of text content makes it easier to understand.', '2008-11-24 13:02:09', '');
INSERT INTO `language_text` VALUES('eng', '_check', '_RATIONALE_132', 'If server-side image maps must be used (e.g., because the geometry of a region cannot be represented with values of the shape attribute), authors must provide the same functionality or information in an alternative accessible format. One way to achieve this is to provide a textual link for each active region so that each link is navigable with the keyboard.', '2008-11-24 13:02:09', '');
INSERT INTO `language_text` VALUES('eng', '_check', '_RATIONALE_14', 'You should not describe an image, or part of an image, using color alone. For example, when referring to a photograph, you should not say "my son is wearing a red coat". Instead say "my son is third from the left wearing a red coat".', '2008-11-24 13:02:09', '');
INSERT INTO `language_text` VALUES('eng', '_check', '_RATIONALE_2', 'Alt text should describe the image. This Alt text is suspicious because it may be just placeholder text.', '2008-11-24 13:02:09', '');
