INSERT INTO `language_text` VALUES('eng', '_check', '_RATIONALE_4', 'All images require a text equivalent (Alt text) but you should set the Alt text to null ("") for decorative images. This image has null Alt text but does not appear to be a decorative image.', '2008-11-24 13:02:09', '');
INSERT INTO `language_text` VALUES('eng', '_check', '_RATIONALE_48', 'The <code>lang</code> attribute allows assistive technology to orient and adapt to the pronunciation and syntax that are specific to the language of the page. This attribute may also play a major role in the emerging global, multi-lingual, simultaneous translation Web environment.', '2008-11-24 13:02:09', '');
INSERT INTO `language_text` VALUES('eng', '_check', '_RATIONALE_49', 'You must use a valid 2 letter or 3 letter language code to identify the primary language of the content.', '2008-11-24 13:02:09', '');
INSERT INTO `language_text` VALUES('eng', '_check', '_RATIONALE_5', 'All images require a text equivalent (Alt text) but you may set the Alt text to all spaces ("  ") for decorative images. This image has spacer Alt text but does not appear to be a decorative image.', '2008-11-24 13:02:09', '');
INSERT INTO `language_text` VALUES('eng', '_check', '_RATIONALE_54', 'All contents, including individual <code>frames</code> in a <code>frameset</code>, should have a <code>title</code> element that defines, in a simple phrase, the purpose of the content.', '2008-11-24 13:02:09', '');
