INSERT INTO `language_text` VALUES('eng', '_check', '_RATIONALE_7', 'If an image is used as a link then it must have alternate (ALT) text that describes the link destination. The Alt text can''t be null or all spaces.', '2008-11-24 13:02:09', '');
INSERT INTO `language_text` VALUES('eng', '_check', '_RATIONALE_71', 'Developers cannot predict how much time a user will require to read a page; premature refresh can disorient users. Content developers should avoid periodic refresh and allow users to choose when they want the latest information.', '2008-11-24 13:02:09', '');
