INSERT INTO `language_text` VALUES('ita', '_check', '_PROCEDURE_109', '1. Verificare la presenza dell''attributo <code>style</code> in ogni elemento.\r\n2. Eliminare o disabilitare qualsiasi attributo <code>style</code>.\r\n3. Visualizzare la pagina e verificare che sia ancora leggibile.', '2009-06-22 14:00:26', '');
