INSERT INTO `language_text` VALUES('ita', '_check', '_PROCEDURE_97', '1. Controllare ogni elemento <code>link</code> con attributo <code>rel</code> uguale a "stylesheet".\r\n2. Eliminare o disabilitare gli elementi individuati.\r\n3. Verificare che il documento sia ancora leggibile.', '2009-06-23 10:55:41', '');
INSERT INTO `language_text` VALUES('ita', '_msgs', 'AC_CONFIRM_DELETE_USER', 'Sei sicuro di voler eliminare gli utenti seguenti? Eliminando un utente si eliminano anche le linee guida e i check, non ancora aperti al pubblico, che egli ha creato. <strong>%s</strong>', '2009-06-23 13:04:44', '');
