INSERT INTO `language_text` VALUES('ita', '_check', '_PROCEDURE_91', '1. Controllare tutti gli elementi <code>select</code> del documento.\r\n2. L''elemento <code>select</code> deve avere un''etichetta associata esplicitamente tramite l''utilizzo dei seguenti metodi.\r\n3. L''attributo <code>for</code> dell''elemento <code>label</code> ha lo stesso valore dell''attributo <code>id</code> dell''elemento <code>select</code>.\r\n4. e/o\r\n5. L''elemento <code>select</code> possiede un attributo <code>title</code>.\r\n6. e/o\r\n7. L''elemento <code>select</code> è contenuto all''interno di un elemento <code>label</code>.', '2009-06-23 10:59:34', '');
INSERT INTO `language_text` VALUES('ita', '_check', '_PROCEDURE_95', '1. Controllare tutti gli elementi <code>textarea</code> del documento.\r\n2. L''elemento <code>textarea</code> deve avere un''etichetta associata esplicitamente tramite l''utilizzo dei seguenti metodi.\r\n3. L''attributo <code>for</code> dell''elemento <code>label</code> ha lo stesso valore dell''attributo <code>id</code> dell''elemento <code>textarea</code>.\r\n4. e/o\r\n5. L''elemento <code>textarea</code> possiede un attributo <code>title</code>.\r\n6. e/o\r\n7. L''elemento <code>textarea</code> è contenuto all''interno di un elemento <code>label</code>.', '2009-06-23 11:00:24', '');
