INSERT INTO `language_text` VALUES('ita', '_msgs', 'AC_HELP_ADD_EDIT_LANGUAGE', '<h2>Inserisci/modifica Lingua</h2>\r\n\r\n<p>Inserire e modificare le proprietà di una lingua permette agli utenti di impostare i campi:\r\n<ul>\r\n<li>Codice della lingua (è un codice ISO-639-2 formato da 3 caratteri)</li>\r\n<li>Locale (un codice arbitrario di 2 lettere per rappresentare una variante della lingua)</li>\r\n<li>Character Set (dovrebbe sempre essere UTF-8)</li>\r\n<li>Espressione Regaolare (lasciare vuoto)</li>\r\n<li>Nome della lingua (in lingua originale)</li>\r\n<li>Nome della lingua in inglese</li>\r\n<li>Stato (abilitato/disabilitato)</li>\r\n</ul>\r\n</p>\r\n<p>Quando "Stato" è abilitato, la lingua è visualizzata nella lista in fondo alle pagine di AChecker, permettendo agli utenti di selezionarla.</p>', '2009-06-24 11:19:11', '');
INSERT INTO `language_text` VALUES('ita', '_msgs', 'AC_HELP_CHANGE_EMAIL', '<h2>Modifica Email</h2>\r\n<p>Questa pagina consente agli utenti di modificare il proprio indirizzo email. </p>', '2009-06-24 11:18:54', '');
