INSERT INTO `language_text` VALUES('ita', '_guideline', '_SUBGROUPNAME_222', '14.2 Integrare il testo con presentazioni grafiche o uditive nei casi in cui esse possano facilitare la comprensione della pagina.', '2009-06-25 12:27:30', '');
