INSERT INTO `language_text` VALUES('ita', '_guideline', '_SUBGROUPNAME_184', '3.5 Usare gli elementi di intestazione per definire la struttura della pagina, usandoli secondo le loro specifiche.', '2009-06-25 12:52:32', '');
INSERT INTO `language_text` VALUES('ita', '_guideline', '_SUBGROUPNAME_183', '3.4 Usare unità di misura relative, anziché assolute, per gli attributi del linguaggio di markup e per le proprietà dei fogli di stile.', '2009-06-25 12:52:47', '');
INSERT INTO `language_text` VALUES('ita', '_guideline', '_SUBGROUPNAME_182', '3.3 Usare i fogli di stile per definire il layout e la parte presentazionale della pagina.', '2009-06-25 12:53:09', '');
INSERT INTO `language_text` VALUES('ita', '_guideline', '_SUBGROUPNAME_181', '3.2 Creare documenti che facciano riferimento a grammatiche formali pubblicate.', '2009-06-25 12:54:21', '');
